-module(dgiot_grpc_test).

-export([start/0, stop/0]).
-export([login/0, logout/0, send/0, test/0]).

start() ->
    Services = #{
        protos => [dgiot_dlink_pb],
        services => #{'dgiot.Dlink' => dgiot_grpc_server}
    },
    {ok, _} = grpc:start_server(server, 30051, Services, []).

stop() ->
    _ = grpc:stop_server(server).

%%https://grpc.io/docs/languages/php/basics/
login() ->
    SvrAddr = "tcp://127.0.0.1:30051",
    {ok, _} = grpc_client_sup:create_channel_pool(<<"channel">>, SvrAddr, #{}).


logout() ->
    _ = grpc_client_sup:stop_channel_pool(<<"channel">>).

send() ->
    Result = dgiot_dlink_client:payload(#{data => <<"Xiao Ming">>, cmd => <<"login">>, product => <<"dsdfsfsdfe">>}, #{channel => <<"channel">>}),
    io:format("Result ~p ~n", [Result]).

test() ->
    FileName = <<"dlink">>,
    Proto = <<"">>,
    {file, Here} = code:is_loaded(?MODULE),
%%    ErlangDir = dgiot_httpc:url_join([filename:dirname(filename:dirname(Here)), "/priv/Erlang/"]),
    Dir = dgiot_httpc:url_join([filename:dirname(filename:dirname(Here)), "/priv/Erlang/"]),
    Name = dgiot_utils:to_list(FileName) ++ ".proto",
    FilePath = Dir ++ Name,
    case file:open(Name, [raw, write, delayed_write, binary]) of
        {ok, Fd} ->
            file:write_file(FilePath, base64:decode(Proto)),
            io:format(" ~s ~p FilePath =  ~p ~n", [?FILE, ?LINE, FilePath]),
            file:close(Fd),
            Opts = [report_warnings, report_errors,
                    {type, all},
                    {protos, ["priv/protos"]},
                    {out_dir, "src/grpc/"},
                    {gpb_opts, [{module_name_prefix, "dgiot_"},
                        {module_name_suffix, "_pb"}]}
                ],
            Result = gpb_compile:file(FilePath, Opts),
            io:format(" ~s ~p Result =  ~p ~n", [?FILE, ?LINE, Result]);
        _ ->
            pass
    end,
%%    Cmd = "cd " ++ ErlangDir ++ " &&  ./rebar3 compile",
%%    io:format(" ~s ~p Cmd = ~p ~n", [?FILE, ?LINE, Cmd]),
%%    os:cmd(Cmd),
    ok.
